import './App.css';
import { useState } from 'react';

// transfers all planes into planeBox on app start
let origData = require("./data.json");
origData.planes.forEach((plane) => {
    plane.spot = "planeBox";
    origData.spots.planeBox.push(plane);
});

Object.prototype.getValue = function(path) {
    return [this].concat(path.split('.')).reduce(function(a, b) { return a[b] });
}

// quick helper function to easily convert to title case
function titleCase(str) {
    return str.toLowerCase().replace("-", " ").split(' ').map(w => w[0].toUpperCase() +
           w.substring(1).toLowerCase()).join(' ');
}

function RampBox({ data, updateData, moveMode, setMoveMode }) {
    return (
        <div id="ramp" className="box">
            <div className="box title-box" style={{"fontSize": 1.5+"em"}}>
                Ramp
            </div>
            <table className="table-list" border="1" frame="void" rules="rows">
                <tbody>
                    <RampBoxItem name="ramp-a" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-b" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-c" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-d" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-e" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-f" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-g" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-h" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-i" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-j" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-k" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-l" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-m" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-n" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-o" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-p" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-q" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                    <RampBoxItem name="ramp-r" data={data} updateData={updateData}
                        moveMode={moveMode} setMoveMode={setMoveMode} />
                </tbody>
            </table>
        </div>
    );
}

function RampBoxItem({ name, data, updateData, moveMode, setMoveMode }) {
    function moveHere() {
        if (moveMode) {
            let planeData;
            data.planes.forEach((plane) => {
                if (plane.tailNumber === moveMode) {
                    planeData = plane;
                }
            });
            console.log(data.spots[planeData.spot]);
            if (Array.isArray(data.spots[planeData.spot])) {
                data.spots[planeData.spot].splice(
                    data.spots[planeData.spot].indexOf(planeData), 1);
            } else {
                data.spots.getValue(planeData.spot).plane = null;
            }
            planeData.spot = name;
            data.spots[name].plane = planeData;
            updateData(data);
            setMoveMode(null);
        }
    }
    let plane;
    if (data.spots[name].plane) {
        plane = (
            <Plane plane={data.spots[name].plane} moveMode={moveMode}
                setMoveMode={setMoveMode} />
        );
    }
    let glow = (moveMode && !data.spots[name].plane ? "glow" : "");
    let upperChar = name.replace("ramp-", "").toUpperCase();
    return (
        <tr id={name} className={glow} onClick={() => moveHere()}>
            <td>{upperChar}</td>
            <td>{plane}</td>
        </tr>
    );
}

function DirectionBox({ data, moveMode }) {
    return (
        <div id="direction" className="box">
            <table className="center-table" border="1" frame="void">
                <tbody>
                    <tr>
                        <DirectionElement name="xc-1" moveMode={moveMode} />
                        <td>
                            <table className="center-table" border="1" frame="void">
                                <tbody>
                                    <tr>
                                        <DirectionElement name="kmcx" moveMode={moveMode} />
                                    </tr>
                                    <tr>
                                        <DirectionElement name="northwest" moveMode={moveMode} />
                                        <DirectionElement name="northeast" moveMode={moveMode} />
                                    </tr>
                                </tbody>
                            </table>
                        </td>
                        <DirectionElement name="xc-2" moveMode={moveMode} />
                    </tr>
                    <tr>
                        <DirectionElement name="xc-3" moveMode={moveMode} />
                        <td>
                            <table className="center-table" border="1" frame="void">
                                <tbody>
                                    <tr>
                                        <DirectionElement name="southwest" moveMode={moveMode} />
                                        <DirectionElement name="southeast" moveMode={moveMode} />
                                    </tr>
                                    <tr>
                                        <DirectionElement name="kcfj" moveMode={moveMode} />
                                        <DirectionElement name="kfkr" moveMode={moveMode} />
                                    </tr>
                                </tbody>
                            </table>
                        </td>
                        <DirectionElement name="xc-4" moveMode={moveMode} />
                    </tr>
                </tbody>
            </table>
        </div>
    );
}

function DirectionElement({ name, moveMode }) {
    let classNameVal = "move-here many";
    let colSpanVal = 1;
    let displayName;
    let widthVal;
    let heightVal;
    if (name.length === 4) {
        displayName = name.toUpperCase();
    } else {
        displayName = titleCase(name);
    }
    if (name.includes("xc")) {
        displayName = "XC";
        classNameVal += " vert";
        widthVal = "75px";
        heightVal = "148px";
    } else {
        heightVal = "70px";
    }
    if (name === "kmcx") {
        colSpanVal = 2;
    }
    let glow = (moveMode ? "glow" : "");
    return (
        <td id={name} className={glow} width={widthVal} height={heightVal} colSpan={colSpanVal}>
            {displayName}
            <div className={classNameVal}></div>
        </td>
    );
}

function HangarsBox({ data }) {
    return (
        <div id="hangars" className="box">
            <table className="center-table" border="1" frame="void">
                <tbody>
                    <tr>
                        <td colSpan="3" className="hangar-header">Hangar 5</td>
                        <td colSpan="3" className="hangar-header">Hangar 6W</td>
                        <td colSpan="3" className="hangar-header">Hangar 6</td>
                    </tr>
                    <tr>
                        <HangarsElement name="h5-1" spotType="full" />
                        <HangarsElement name="h5-2" spotType="full" />
                        <HangarsElement name="h5-3" spotType="full" />
                        <HangarsElement name="h6w-1" spotType="full" />
                        <td>
                            <table className="center-table" border="1" frame="void">
                                <tbody>
                                    <tr>
                                        <HangarsElement name="h6w-7" spotType="half" />
                                    </tr>
                                    <tr>
                                        <HangarsElement name="h6w-8" spotType="half" />
                                    </tr>
                                </tbody>
                            </table>
                        </td>
                        <HangarsElement name="h6w-4" spotType="full" />
                        <HangarsElement name="h6-1" spotType="full" />
                        <HangarsElement name="h6-2" spotType="full" />
                        <HangarsElement name="h6-3" spotType="full" />
                    </tr>
                    <tr>
                        <HangarsElement name="h5-4" spotType="full" />
                        <HangarsElement name="h5-5" spotType="full" />
                        <HangarsElement name="h5-6" spotType="full" />
                        <HangarsElement name="h6w-2" spotType="full" />
                        <td>
                            <table className="center-table" border="1" frame="void">
                                <tbody>
                                    <tr>
                                        <HangarsElement name="h6w-9" spotType="half" />
                                    </tr>
                                    <tr>
                                        <HangarsElement name="h6w-10" spotType="half" />
                                    </tr>
                                </tbody>
                            </table>
                        </td>
                        <HangarsElement name="h6w-5" spotType="full" />
                        <td className="empty"></td>
                        <HangarsElement name="h6-4" spotType="full" />
                        <td className="empty"></td>
                    </tr>
                    <tr>
                        <td className="empty"></td>
                        <HangarsElement name="h5-7" spotType="full" />
                        <td className="empty"></td>
